window.medicalTermsApp={currentCardIndex:0,isFlipped:!1,studyStats:JSON.parse(localStorage.getItem("medicalTermsStats")||'{"studied": 0, "correct": 0}'),saveStats(){localStorage.setItem("medicalTermsStats",JSON.stringify(this.studyStats)),this.updateStatsDisplay()},updateStatsDisplay(){const t=document.querySelector('[data-stat="studied"]'),e=document.querySelector('[data-stat="correct"]'),s=document.querySelector('[data-stat="accuracy"]');if(t&&(t.textContent=this.studyStats.studied),e&&(e.textContent=this.studyStats.correct),s){const a=this.studyStats.studied>0?Math.round(this.studyStats.correct/this.studyStats.studied*100):0;s.textContent=a+"%"}}};document.addEventListener("DOMContentLoaded",()=>{window.medicalTermsApp.updateStatsDisplay()});
